import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Users, UserCheck, UserX, Coffee, AlertTriangle, Search, Plus, Edit, Trash2, Calendar, Filter, Download, ShieldAlert } from "lucide-react";
import { format } from "date-fns";
import { authService, usePermission } from "@/lib/auth";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { apiRequest } from "@/lib/queryClient";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger, DropdownMenuSeparator } from "@/components/ui/dropdown-menu";
import { MoreVertical, Eye, FileText } from "lucide-react";

interface AttendanceSummary {
  todayPresent: number;
  todayAbsent: number;
  todayLeave: number;
  todayWeeklyOff: number;
  continuouslyAbsent: number;
}

interface EmployeeAttendanceData {
  id: number;
  payCode: string;
  employeeName: string;
  department: string;
  present: number;
  weeklyOff: number;
  leave: number;
  holidays: number;
  payDays: number;
  otHours: number;
}

export default function AttendancePage() {
  const user = authService.getUser();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Check for attendance permission (using correct permission names)
  const { hasPermission: canViewAttendance, loading: permissionLoading } = usePermission('attendance_view_all');
  const { hasPermission: canEditAttendance } = usePermission('attendance_edit');

  // State for filters and search
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [searchTerm, setSearchTerm] = useState("");
  const [isQuickEntryOpen, setIsQuickEntryOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState("");
  const [payDays, setPayDays] = useState("");
  const [otHours, setOtHours] = useState("");

  // Edit dialog state
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState<EmployeeAttendanceData | null>(null);
  const [editPayDays, setEditPayDays] = useState("");
  const [editOtHours, setEditOtHours] = useState("");

  // View dialog state
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const [viewingEmployee, setViewingEmployee] = useState<EmployeeAttendanceData | null>(null);

  // Get employees for the company
  const { data: employees = [] } = useQuery({
    queryKey: [`/api/employees/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  // Get departments
  const { data: departments = [] } = useQuery({
    queryKey: [`/api/departments/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  // Get attendance summary data from API
  const { data: summaryData } = useQuery<AttendanceSummary>({
    queryKey: ['/api/attendance/summary/today'],
    enabled: !!user?.companyId,
  });

  const defaultSummary: AttendanceSummary = {
    todayPresent: 0,
    todayAbsent: 0,
    todayLeave: 0,
    todayWeeklyOff: 0,
    continuouslyAbsent: 0
  };

  // Get real employee attendance data from API
  const { data: attendanceData = [] } = useQuery<EmployeeAttendanceData[]>({
    queryKey: [`/api/attendance/employee-data/${user?.companyId}/${selectedYear}/${selectedMonth}`],
    enabled: !!user?.companyId,
  });

  // Filter employees based on search term
  const filteredData = attendanceData.filter(emp =>
    emp.employeeName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.payCode.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.department.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Quick Entry mutation
  const quickEntryMutation = useMutation({
    mutationFn: async (data: { employeeId: string; payDays: string; otHours: string }) => {
      const token = authService.getToken();
      if (!token) {
        throw new Error('Authentication required');
      }

      const response = await fetch('/api/attendance/quick-entry', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          employeeId: parseInt(data.employeeId),
          payDays: parseInt(data.payDays),
          otHours: parseFloat(data.otHours),
          month: selectedMonth,
          year: selectedYear
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to update attendance');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Attendance data updated successfully",
      });
      setIsQuickEntryOpen(false);
      setSelectedEmployee("");
      setPayDays("");
      setOtHours("");
      queryClient.invalidateQueries({ queryKey: [`/api/employees/${user?.companyId}`] });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance/summary/today'] });
      queryClient.invalidateQueries({ queryKey: [`/api/attendance/employee-data/${user?.companyId}/${selectedYear}/${selectedMonth}`] });
    },
    onError: (error: any) => {
      console.error('Quick entry error:', error);
      if (error.message?.includes('Invalid or expired token') || error.message?.includes('401') || error.message?.includes('403')) {
        toast({
          title: "Session Expired",
          description: "Please refresh the page and log in again",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Error",
          description: error.message || "Failed to update attendance data",
          variant: "destructive",
        });
      }
    },
  });

  const handleQuickEntry = () => {
    if (!selectedEmployee || !payDays || !otHours) {
      toast({
        title: "Validation Error",
        description: "Please fill all required fields",
        variant: "destructive",
      });
      return;
    }
    quickEntryMutation.mutate({ employeeId: selectedEmployee, payDays, otHours });
  };

  // Edit Employee mutation
  const editEmployeeMutation = useMutation({
    mutationFn: async (data: { employeeId: number; payDays: string; otHours: string }) => {
      const token = authService.getToken();
      if (!token) {
        throw new Error('Authentication required');
      }

      const response = await fetch('/api/attendance/quick-entry', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          employeeId: data.employeeId,
          payDays: parseInt(data.payDays),
          otHours: parseFloat(data.otHours),
          month: selectedMonth,
          year: selectedYear
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to update attendance');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Employee attendance updated successfully",
      });
      setIsEditDialogOpen(false);
      setEditingEmployee(null);
      setEditPayDays("");
      setEditOtHours("");
      queryClient.invalidateQueries({ queryKey: [`/api/attendance/employee-data/${user?.companyId}/${selectedYear}/${selectedMonth}`] });
    },
    onError: (error: any) => {
      console.error('Edit employee error:', error);
      if (error.message?.includes('Invalid or expired token') || error.message?.includes('401') || error.message?.includes('403')) {
        toast({
          title: "Session Expired",
          description: "Please refresh the page and log in again",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Error",
          description: error.message || "Failed to update attendance data",
          variant: "destructive",
        });
      }
    },
  });

  // Delete Employee mutation
  const deleteEmployeeMutation = useMutation({
    mutationFn: async (employeeId: number) => {
      const token = authService.getToken();
      if (!token) {
        throw new Error('Authentication required');
      }

      const response = await fetch(`/api/attendance/delete/${employeeId}/${selectedYear}/${selectedMonth}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to delete attendance record');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Attendance record deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/attendance/employee-data/${user?.companyId}/${selectedYear}/${selectedMonth}`] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete attendance record",
        variant: "destructive",
      });
    },
  });

  const handleEditEmployee = (employee: EmployeeAttendanceData) => {
    setEditingEmployee(employee);
    setEditPayDays(employee.payDays.toString());
    setEditOtHours(employee.otHours.toString());
    setIsEditDialogOpen(true);
  };

  const handleViewEmployee = (employee: EmployeeAttendanceData) => {
    setViewingEmployee(employee);
    setIsViewDialogOpen(true);
  };

  const handleDeleteEmployee = (employee: EmployeeAttendanceData) => {
    if (window.confirm(`Are you sure you want to delete attendance record for ${employee.employeeName}?`)) {
      deleteEmployeeMutation.mutate(employee.id);
    }
  };

  const submitQuickEntry = () => {
    if (!selectedEmployee || !payDays || !otHours) {
      toast({
        title: "Validation Error",
        description: "Please fill all required fields",
        variant: "destructive",
      });
      return;
    }
    quickEntryMutation.mutate({ employeeId: selectedEmployee, payDays, otHours });
  };

  const submitEdit = () => {
    if (!editingEmployee || !editPayDays || !editOtHours) {
      toast({
        title: "Validation Error",
        description: "Please fill all required fields",
        variant: "destructive",
      });
      return;
    }
    editEmployeeMutation.mutate({ 
      employeeId: editingEmployee.id, 
      payDays: editPayDays, 
      otHours: editOtHours 
    });
  };

  const handleExportToExcel = () => {
    const token = localStorage.getItem('token');
    const url = `/api/attendance/export/${user?.companyId}/${selectedYear}/${selectedMonth}`;

    // Create a temporary link with auth header
    fetch(url, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    .then(response => response.blob())
    .then(blob => {
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `attendance_${selectedYear}_${selectedMonth}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    })
    .catch(error => {
      toast({
        title: "Error",
        description: "Failed to export attendance data",
        variant: "destructive",
      });
    });
  };

  const months = [
    { value: 1, label: "January" },
    { value: 2, label: "February" },
    { value: 3, label: "March" },
    { value: 4, label: "April" },
    { value: 5, label: "May" },
    { value: 6, label: "June" },
    { value: 7, label: "July" },
    { value: 8, label: "August" },
    { value: 9, label: "September" },
    { value: 10, label: "October" },
    { value: 11, label: "November" },
    { value: 12, label: "December" },
  ];

  const years = Array.from({ length: 5 }, (_, i) => new Date().getFullYear() - 2 + i);

  // Permission guard - show access denied if user doesn't have permission
  if (permissionLoading) {
    return (
      <div className="container mx-auto p-6 space-y-6 max-w-7xl">
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div>
            <p className="mt-2 text-gray-600">Checking permissions...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!canViewAttendance && user?.role === 'employee') {
    return (
      <div className="container mx-auto p-6 space-y-6 max-w-7xl">
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <ShieldAlert className="h-16 w-16 text-red-500 mx-auto mb-4" />
            <h1 className="text-2xl font-bold text-gray-900 mb-2">Access Denied</h1>
            <p className="text-gray-600 mb-4">You don't have permission to access attendance management.</p>
            <p className="text-sm text-gray-500">Please request the "attendance_view_all" permission from your administrator.</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6 max-w-7xl">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Attendance Management</h1>
          <p className="text-muted-foreground">Monitor and manage employee attendance records</p>
        </div>
        <div className="flex gap-4 items-center">
          <Button 
            onClick={handleExportToExcel} 
            variant="outline"
            className="flex items-center gap-2"
          >
            <Download className="h-4 w-4" />
            Export to Excel
          </Button>
          <div className="text-right">
            <p className="text-sm text-muted-foreground">Today</p>
            <p className="text-lg font-semibold">{format(new Date(), 'EEEE, MMM dd, yyyy')}</p>
          </div>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
        <Card className="border-2 border-green-200 bg-gradient-to-br from-green-50 to-green-100">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-green-700">Today Present</CardTitle>
            <UserCheck className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-800">{summaryData?.todayPresent || defaultSummary.todayPresent}</div>
            <p className="text-xs text-green-600">Active employees</p>
          </CardContent>
        </Card>

        <Card className="border-2 border-red-200 bg-gradient-to-br from-red-50 to-red-100">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-red-700">Today Absent</CardTitle>
            <UserX className="h-4 w-4 text-red-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-red-800">{summaryData?.todayAbsent || defaultSummary.todayAbsent}</div>
            <p className="text-xs text-red-600">Missing today</p>
          </CardContent>
        </Card>

        <Card className="border-2 border-blue-200 bg-gradient-to-br from-blue-50 to-blue-100">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-blue-700">Today Leave</CardTitle>
            <Coffee className="h-4 w-4 text-blue-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-800">{summaryData?.todayLeave || defaultSummary.todayLeave}</div>
            <p className="text-xs text-blue-600">On planned leave</p>
          </CardContent>
        </Card>

        <Card className="border-2 border-purple-200 bg-gradient-to-br from-purple-50 to-purple-100">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-purple-700">Today Weekly Off</CardTitle>
            <Calendar className="h-4 w-4 text-purple-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-purple-800">{summaryData?.todayWeeklyOff || defaultSummary.todayWeeklyOff}</div>
            <p className="text-xs text-purple-600">Weekly off day</p>
          </CardContent>
        </Card>

        <Card className="border-2 border-orange-200 bg-gradient-to-br from-orange-50 to-orange-100">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-orange-700">Continuously Absent</CardTitle>
            <AlertTriangle className="h-4 w-4 text-orange-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-800">{summaryData?.continuouslyAbsent || defaultSummary.continuouslyAbsent}</div>
            <p className="text-xs text-orange-600">Since last 7 days</p>
          </CardContent>
        </Card>
      </div>

      {/* Filters and Quick Entry */}
      <div className="flex flex-col sm:flex-row gap-4 items-center justify-between bg-gray-50 p-4 rounded-lg">
        <div className="flex flex-col sm:flex-row gap-4 items-center">
          {/* Month Selection */}
          <div className="flex items-center gap-2">
            <Calendar className="h-4 w-4 text-gray-600" />
            <Select value={selectedMonth.toString()} onValueChange={(value) => setSelectedMonth(parseInt(value))}>
              <SelectTrigger className="w-32">
                <SelectValue placeholder="Month" />
              </SelectTrigger>
              <SelectContent>
                {months.map((month) => (
                  <SelectItem key={month.value} value={month.value.toString()}>
                    {month.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Year Selection */}
          <Select value={selectedYear.toString()} onValueChange={(value) => setSelectedYear(parseInt(value))}>
            <SelectTrigger className="w-24">
              <SelectValue placeholder="Year" />
            </SelectTrigger>
            <SelectContent>
              {years.map((year) => (
                <SelectItem key={year} value={year.toString()}>
                  {year}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          {/* Search Filter */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search employees..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 w-64"
            />
          </div>
        </div>

        {/* Quick Entry Button */}
        <Dialog open={isQuickEntryOpen} onOpenChange={setIsQuickEntryOpen}>
          <DialogTrigger asChild>
            <Button className="bg-blue-600 hover:bg-blue-700">
              <Plus className="h-4 w-4 mr-2" />
              Quick Entry
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>Quick Attendance Entry</DialogTitle>
              <DialogDescription>
                Add attendance data for an employee quickly
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="employee">Employee</Label>
                <Select value={selectedEmployee} onValueChange={setSelectedEmployee}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select employee..." />
                  </SelectTrigger>
                  <SelectContent>
                    {employees.map((emp: any) => (
                      <SelectItem key={emp.id} value={emp.id.toString()}>
                        {emp.firstName} {emp.lastName} ({emp.employeeId})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="payDays">Pay Days</Label>
                <Input
                  id="payDays"
                  type="number"
                  placeholder="Enter pay days"
                  value={payDays}
                  onChange={(e) => setPayDays(e.target.value)}
                />
              </div>
              <div>
                <Label htmlFor="otHours">OT Hours</Label>
                <Input
                  id="otHours"
                  type="number"
                  step="0.1"
                  placeholder="Enter OT hours"
                  value={otHours}
                  onChange={(e) => setOtHours(e.target.value)}
                />
              </div>
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => setIsQuickEntryOpen(false)}>
                  Cancel
                </Button>
                <Button onClick={handleQuickEntry} disabled={quickEntryMutation.isPending}>
                  {quickEntryMutation.isPending ? "Saving..." : "Save"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Employee Attendance Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Users className="h-5 w-5 text-blue-600" />
            Employee Attendance Data - {months.find(m => m.value === selectedMonth)?.label} {selectedYear}
          </CardTitle>
          <CardDescription>
            Detailed attendance information for all employees
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Pay Code</TableHead>
                  <TableHead>Employee Name</TableHead>
                  <TableHead>Department</TableHead>
                  <TableHead className="text-center">Present</TableHead>
                  <TableHead className="text-center">Weekly Off</TableHead>
                  <TableHead className="text-center">Leave</TableHead>
                  <TableHead className="text-center">Holidays</TableHead>
                  <TableHead className="text-center">Pay Days</TableHead>
                  <TableHead className="text-center">OT Hours</TableHead>
                  <TableHead className="text-center">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredData.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={10} className="text-center py-8 text-muted-foreground">
                      No employees found matching your search criteria
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredData.map((employee) => (
                    <TableRow key={employee.id}>
                      <TableCell className="font-medium">{employee.payCode}</TableCell>
                      <TableCell>{employee.employeeName}</TableCell>
                      <TableCell>{employee.department}</TableCell>
                      <TableCell className="text-center">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                          {employee.present}
                        </span>
                      </TableCell>
                      <TableCell className="text-center">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                          {employee.weeklyOff}
                        </span>
                      </TableCell>
                      <TableCell className="text-center">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {employee.leave}
                        </span>
                      </TableCell>
                      <TableCell className="text-center">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-orange-100 text-orange-800">
                          {employee.holidays}
                        </span>
                      </TableCell>
                      <TableCell className="text-center">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800 font-semibold">
                          {employee.payDays}
                        </span>
                      </TableCell>
                      <TableCell className="text-center">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-indigo-100 text-indigo-800">
                          {employee.otHours}
                        </span>
                      </TableCell>
                      <TableCell className="text-center">
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="outline" size="sm">
                              <MoreVertical className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => handleViewEmployee(employee)}>
                              <Eye className="h-4 w-4 mr-2" />
                              View Details
                            </DropdownMenuItem>
                            <DropdownMenuItem onClick={() => handleEditEmployee(employee)}>
                              <Edit className="h-4 w-4 mr-2" />
                              Edit Record
                            </DropdownMenuItem>
                            <DropdownMenuItem onClick={() => window.open(`/admin/attendance/daily-log/${employee.id}/${selectedYear}/${selectedMonth}`, '_blank')}>
                              <FileText className="h-4 w-4 mr-2" />
                              Daily Log
                            </DropdownMenuItem>
                            <DropdownMenuSeparator />
                            <DropdownMenuItem 
                              className="text-red-600"
                              onClick={() => handleDeleteEmployee(employee)}
                            >
                              <Trash2 className="h-4 w-4 mr-2" />
                              Delete Record
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      {/* Quick Entry Dialog */}
      <Dialog open={isQuickEntryOpen} onOpenChange={setIsQuickEntryOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Quick Entry</DialogTitle>
            <DialogDescription>
              Add attendance data for an employee
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="employee">Employee</Label>
              <Select value={selectedEmployee} onValueChange={setSelectedEmployee}>
                <SelectTrigger>
                  <SelectValue placeholder="Select employee" />
                </SelectTrigger>
                <SelectContent>
                  {employees.map((emp: any) => (
                    <SelectItem key={emp.id} value={emp.id.toString()}>
                      {emp.firstName} {emp.lastName} ({emp.employeeId})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="payDays">Pay Days</Label>
              <Input
                id="payDays"
                type="number"
                placeholder="Enter pay days"
                value={payDays}
                onChange={(e) => setPayDays(e.target.value)}
              />
            </div>
            <div>
              <Label htmlFor="otHours">OT Hours</Label>
              <Input
                id="otHours"
                type="number"
                step="0.1"
                placeholder="Enter OT hours"
                value={otHours}
                onChange={(e) => setOtHours(e.target.value)}
              />
            </div>
            <div className="flex gap-2 justify-end">
              <Button variant="outline" onClick={() => setIsQuickEntryOpen(false)}>
                Cancel
              </Button>
              <Button onClick={submitQuickEntry} disabled={quickEntryMutation.isPending}>
                {quickEntryMutation.isPending ? "Saving..." : "Save"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Edit Employee Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Edit Attendance Record</DialogTitle>
            <DialogDescription>
              Update attendance data for {editingEmployee?.employeeName}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="editPayDays">Pay Days</Label>
              <Input
                id="editPayDays"
                type="number"
                placeholder="Enter pay days"
                value={editPayDays}
                onChange={(e) => setEditPayDays(e.target.value)}
              />
            </div>
            <div>
              <Label htmlFor="editOtHours">OT Hours</Label>
              <Input
                id="editOtHours"
                type="number"
                step="0.1"
                placeholder="Enter OT hours"
                value={editOtHours}
                onChange={(e) => setEditOtHours(e.target.value)}
              />
            </div>
            <div className="flex gap-2 justify-end">
              <Button variant="outline" onClick={() => setIsEditDialogOpen(false)}>
                Cancel
              </Button>
              <Button onClick={submitEdit} disabled={editEmployeeMutation.isPending}>
                {editEmployeeMutation.isPending ? "Saving..." : "Update"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* View Employee Details Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="sm:max-w-lg">
          <DialogHeader>
            <DialogTitle>Employee Attendance Details</DialogTitle>
            <DialogDescription>
              Detailed view for {viewingEmployee?.employeeName} - {months.find(m => m.value === selectedMonth)?.label} {selectedYear}
            </DialogDescription>
          </DialogHeader>
          {viewingEmployee && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Pay Code</Label>
                  <p className="text-lg font-semibold">{viewingEmployee.payCode}</p>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Department</Label>
                  <p className="text-lg font-semibold">{viewingEmployee.department}</p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Present Days</Label>
                  <p className="text-2xl font-bold text-green-600">{viewingEmployee.present}</p>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Weekly Off</Label>
                  <p className="text-2xl font-bold text-purple-600">{viewingEmployee.weeklyOff}</p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Leave Days</Label>
                  <p className="text-2xl font-bold text-blue-600">{viewingEmployee.leave}</p>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Holidays</Label>
                  <p className="text-2xl font-bold text-orange-600">{viewingEmployee.holidays}</p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">Total Pay Days</Label>
                  <p className="text-2xl font-bold text-gray-800">{viewingEmployee.payDays}</p>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-gray-600">OT Hours</Label>
                  <p className="text-2xl font-bold text-indigo-600">{viewingEmployee.otHours}</p>
                </div>
              </div>

              <div className="flex justify-end pt-4">
                <Button onClick={() => setIsViewDialogOpen(false)}>Close</Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/client-list.tsx.content:
import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { Plus, MoreHorizontal, Edit, Users, Calendar, DollarSign } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/lib/auth';
import { useLocation } from 'wouter';

const clientSchema = z.object({
  projectName: z.string().min(1, 'Project name is required'),
  projectCost: z.string().min(1, 'Project cost is required'),
  startDate: z.string().min(1, 'Start date is required'),
  principalEmployer: z.string().optional(),
  clientName: z.string().optional(),
  natureAndLocationOfWork: z.string().optional(),
});

type ClientFormData = z.infer<typeof clientSchema>;

export default function ClientList() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingClient, setEditingClient] = useState<any>(null);

  const { data: clients = [], isLoading } = useQuery<any[]>({
    queryKey: ['/api/clients', user?.companyId],
    enabled: !!user?.companyId,
  });

  const form = useForm<ClientFormData>({
    resolver: zodResolver(clientSchema),
    defaultValues: {
      projectName: '',
      projectCost: '',
      startDate: '',
      principalEmployer: '',
      clientName: '',
      natureAndLocationOfWork: '',
    },
  });

  const createClientMutation = useMutation({
    mutationFn: async (data: ClientFormData) => {
      const response = await apiRequest('POST', '/api/clients', {
        ...data,
        companyId: user?.companyId,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/clients'] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: 'Success',
        description: 'Client project created successfully',
      });
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'Failed to create client project',
        variant: 'destructive',
      });
    },
  });

  const updateClientMutation = useMutation({
    mutationFn: async (data: ClientFormData & { id: number }) => {
      const response = await apiRequest('PUT', `/api/clients/${data.id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/clients'] });
      setIsDialogOpen(false);
      setEditingClient(null);
      form.reset();
      toast({
        title: 'Success',
        description: 'Client project updated successfully',
      });
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'Failed to update client project',
        variant: 'destructive',
      });
    },
  });

  const onSubmit = (data: ClientFormData) => {
    if (editingClient) {
      updateClientMutation.mutate({ ...data, id: editingClient.id });
    } else {
      createClientMutation.mutate(data);
    }
  };

  const handleEdit = (client: any) => {
    setEditingClient(client);
    form.reset({
      projectName: client.projectName,
      projectCost: client.projectCost,
      startDate: client.startDate?.split('T')[0] || '',
      principalEmployer: client.principalEmployer || '',
      clientName: client.clientName || '',
      natureAndLocationOfWork: client.natureAndLocationOfWork || '',
    });
    setIsDialogOpen(true);
  };

  const handleAddNew = () => {
    setEditingClient(null);
    form.reset();
    setIsDialogOpen(true);
  };

  const handleManageEmployee = (client: any) => {
    // Navigate to manage employee page with client project context
    setLocation(`/admin/manage-employee?projectId=${client.id}&projectName=${encodeURIComponent(client.projectName)}`);
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="space-y-6 p-6">
      <div className="flex justify-between items-center">
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleAddNew}>
              <Plus className="w-4 h-4 mr-2" />
              Add Client Project
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-lg max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>
                {editingClient ? 'Edit Client Project' : 'Add New Client Project'}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <div>
                <Label htmlFor="projectName">Project Name</Label>
                <Input
                  id="projectName"
                  {...form.register('projectName')}
                  placeholder="Enter project name"
                />
                {form.formState.errors.projectName && (
                  <p className="text-red-500 text-sm mt-1">
                    {form.formState.errors.projectName.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="projectCost">Project Cost</Label>
                <Input
                  id="projectCost"
                  {...form.register('projectCost')}
                  placeholder="Enter project cost"
                />
                {form.formState.errors.projectCost && (
                  <p className="text-red-500 text-sm mt-1">
                    {form.formState.errors.projectCost.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="startDate">Start Date</Label>
                <Input
                  id="startDate"
                  type="date"
                  {...form.register('startDate')}
                />
                {form.formState.errors.startDate && (
                  <p className="text-red-500 text-sm mt-1">
                    {form.formState.errors.startDate.message}
                  </p>
                )}
              </div>

              <div>
                <Label htmlFor="principalEmployer">Principal Employer</Label>
                <Input
                  id="principalEmployer"
                  {...form.register('principalEmployer')}
                  placeholder="Enter principal employer"
                />
              </div>

              <div>
                <Label htmlFor="clientName">Client Name</Label>
                <Input
                  id="clientName"
                  {...form.register('clientName')}
                  placeholder="Enter client name"
                />
              </div>

              <div>
                <Label htmlFor="natureAndLocationOfWork">Nature & Location of Work</Label>
                <Input
                  id="natureAndLocationOfWork"
                  {...form.register('natureAndLocationOfWork')}
                  placeholder="Enter nature and location of work"
                />
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createClientMutation.isPending || updateClientMutation.isPending}
                >
                  {createClientMutation.isPending || updateClientMutation.isPending
                    ? 'Saving...'
                    : editingClient
                    ? 'Update'
                    : 'Create'}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <DollarSign className="w-5 h-5 mr-2 text-blue-600" />
            Client Projects Overview
          </CardTitle>
          <CardDescription>
            Manage and track all your client projects in one place
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Project Name</TableHead>
                <TableHead>Project Cost</TableHead>
                <TableHead>Start Date</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {clients.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={4} className="text-center py-8 text-gray-500">
                    No client projects found. Create your first project to get started.
                  </TableCell>
                </TableRow>
              ) : (
                clients.map((client: any) => (
                  <TableRow key={client.id}>
                    <TableCell className="font-medium">{client.projectName}</TableCell>
                    <TableCell>‚Çπ{client.projectCost}</TableCell>
                    <TableCell>
                      {client.startDate
                        ? new Date(client.startDate).toLocaleDateString()
                        : 'Not set'}
                    </TableCell>
                    <TableCell className="text-right">
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button variant="ghost" className="h-8 w-8 p-0">
                            <MoreHorizontal className="h-4 w-4" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                          <DropdownMenuItem onClick={() => handleEdit(client)}>
                            <Edit className="w-4 h-4 mr-2" />
                            Edit
                          </DropdownMenuItem>
                          <DropdownMenuItem onClick={() => handleManageEmployee(client)}>
                            <Users className="w-4 h-4 mr-2" />
                            Manage Employee
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/company-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Building2, Plus, Edit, Search, CheckCircle, XCircle, Clock, Trash2 } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useAuth } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";

export default function CompanyManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingCompany, setEditingCompany] = useState<any>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);

  const { data: companies = [], isLoading } = useQuery({
    queryKey: ['/api/admin/companies'],
  });

  const createCompanyMutation = useMutation({
    mutationFn: (companyData: any) => apiRequest('POST', '/api/admin/companies', companyData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/companies'] });
      setIsCreateDialogOpen(false);
      toast({ title: "Company created successfully" });
    },
    onError: () => {
      toast({ title: "Failed to create company", variant: "destructive" });
    },
  });

  const updateCompanyMutation = useMutation({
    mutationFn: ({ id, ...data }: any) => apiRequest('PUT', `/api/admin/companies/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/companies'] });
      toast({ title: "Company updated successfully" });
    },
    onError: () => {
      toast({ title: "Failed to update company", variant: "destructive" });
    },
  });

  const approveCompanyMutation = useMutation({
    mutationFn: ({ id, action }: { id: number; action: 'approve' | 'reject' }) => 
      apiRequest('PUT', `/api/admin/companies/${id}/approve`, { action }),
    onSuccess: (_, { action }) => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/companies'] });
      toast({ 
        title: `Company ${action === 'approve' ? 'approved' : 'rejected'} successfully`,
        variant: action === 'approve' ? 'default' : 'destructive'
      });
    },
    onError: () => {
      toast({ title: "Failed to process approval", variant: "destructive" });
    },
  });

  const deleteCompanyMutation = useMutation({
    mutationFn: (id: number) => apiRequest('DELETE', `/api/admin/companies/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/companies'] });
      toast({ title: "Company deleted successfully" });
    },
    onError: () => {
      toast({ title: "Failed to delete company", variant: "destructive" });
    },
  });

  const filteredCompanies = companies.filter((c: any) =>
    c.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    c.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCreateCompany = (formData: FormData) => {
    const companyData = {
      name: formData.get('name'),
      email: formData.get('email'),
      phone: formData.get('phone'),
      address: formData.get('address'),
      status: 'approved',
    };
    createCompanyMutation.mutate(companyData);
  };

  const handleEditCompany = (formData: FormData) => {
    const companyData = {
      id: editingCompany.id,
      name: formData.get('name'),
      email: formData.get('email'),
      phone: formData.get('phone'),
      address: formData.get('address'),
      status: formData.get('status'),
    };
    updateCompanyMutation.mutate(companyData);
    setIsEditDialogOpen(false);
    setEditingCompany(null);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200"><Clock className="w-3 h-3 mr-1" />Pending</Badge>;
      case 'approved':
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200"><CheckCircle className="w-3 h-3 mr-1" />Approved</Badge>;
      case 'rejected':
        return <Badge variant="destructive"><XCircle className="w-3 h-3 mr-1" />Rejected</Badge>;
      case 'suspended':
        return <Badge variant="secondary">Suspended</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  if (user?.role !== 'system_admin') {
    return (
      <div className="flex items-center justify-center h-64">
        <p className="text-muted-foreground">System admin access required</p>
      </div>
    );
  }

  if (isLoading) {
    return <div>Loading companies...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Company Management</h1>
          <p className="text-muted-foreground">
            Manage all companies in the system
          </p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="mr-2 h-4 w-4" />
              Add Company
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Company</DialogTitle>
            </DialogHeader>
            <form onSubmit={(e) => {
              e.preventDefault();
              handleCreateCompany(new FormData(e.currentTarget));
            }} className="space-y-4">
              <div>
                <label className="text-sm font-medium">Company Name</label>
                <Input name="name" required />
              </div>
              <div>
                <label className="text-sm font-medium">Email</label>
                <Input name="email" type="email" required />
              </div>
              <div>
                <label className="text-sm font-medium">Phone</label>
                <Input name="phone" />
              </div>
              <div>
                <label className="text-sm font-medium">Address</label>
                <Input name="address" />
              </div>
              <div className="flex gap-2">
                <Button type="submit" disabled={createCompanyMutation.isPending}>
                  {createCompanyMutation.isPending ? 'Creating...' : 'Create Company'}
                </Button>
                <Button type="button" variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
                  Cancel
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>

        {/* Edit Company Dialog */}
        <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Edit Company</DialogTitle>
            </DialogHeader>
            {editingCompany && (
              <form onSubmit={(e) => {
                e.preventDefault();
                handleEditCompany(new FormData(e.currentTarget));
              }} className="space-y-4">
                <div>
                  <Label className="text-sm font-medium">Company Name</Label>
                  <Input name="name" defaultValue={editingCompany.name} required />
                </div>
                <div>
                  <Label className="text-sm font-medium">Email</Label>
                  <Input name="email" type="email" defaultValue={editingCompany.email} required />
                </div>
                <div>
                  <Label className="text-sm font-medium">Phone</Label>
                  <Input name="phone" defaultValue={editingCompany.phone || ''} />
                </div>
                <div>
                  <Label className="text-sm font-medium">Address</Label>
                  <Input name="address" defaultValue={editingCompany.address || ''} />
                </div>
                <div>
                  <Label className="text-sm font-medium">Status</Label>
                  <Select name="status" defaultValue={editingCompany.status}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="pending">Pending</SelectItem>
                      <SelectItem value="approved">Approved</SelectItem>
                      <SelectItem value="rejected">Rejected</SelectItem>
                      <SelectItem value="suspended">Suspended</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex gap-2">
                  <Button type="submit" disabled={updateCompanyMutation.isPending}>
                    {updateCompanyMutation.isPending ? 'Updating...' : 'Update Company'}
                  </Button>
                  <Button type="button" variant="outline" onClick={() => {
                    setIsEditDialogOpen(false);
                    setEditingCompany(null);
                  }}>
                    Cancel
                  </Button>
                </div>
              </form>
            )}
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Companies</CardTitle>
          <div className="flex items-center space-x-2">
            <Search className="h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search companies..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="max-w-sm"
            />
          </div>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Company</TableHead>
                <TableHead>Contact</TableHead>
                <TableHead>Profile</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Created</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredCompanies.map((company: any) => (
                <TableRow key={company.id}>
                  <TableCell>
                    <div>
                      <div className="font-medium">{company.name}</div>
                      <div className="text-sm text-muted-foreground">{company.address}</div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <div className="text-sm">{company.email}</div>
                      <div className="text-sm text-muted-foreground">{company.phone}</div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={company.profileComplete ? 'default' : 'outline'}>
                      {company.profileComplete ? 'Complete' : 'Incomplete'}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    {getStatusBadge(company.status)}
                  </TableCell>
                  <TableCell>
                    {new Date(company.createdAt).toLocaleDateString()}
                  </TableCell>
                  <TableCell>
                    <div className="flex gap-2">
                      {company.status === 'pending' ? (
                        <>
                          <Button 
                            size="sm" 
                            className="bg-green-600 hover:bg-green-700 text-white"
                            onClick={() => approveCompanyMutation.mutate({ id: company.id, action: 'approve' })}
                            disabled={approveCompanyMutation.isPending}
                          >
                            <CheckCircle className="h-3 w-3 mr-1" />
                            Approve
                          </Button>
                          <Button 
                            size="sm" 
                            variant="destructive"
                            onClick={() => approveCompanyMutation.mutate({ id: company.id, action: 'reject' })}
                            disabled={approveCompanyMutation.isPending}
                          >
                            <XCircle className="h-3 w-3 mr-1" />
                            Reject
                          </Button>
                        </>
                      ) : (
                        <>
                          <Button 
                            size="sm" 
                            variant="outline"
                            onClick={() => {
                              setEditingCompany(company);
                              setIsEditDialogOpen(true);
                            }}
                          >
                            <Edit className="h-3 w-3 mr-1" />
                            Edit
                          </Button>
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button size="sm" variant="destructive">
                                <Trash2 className="h-3 w-3 mr-1" />
                                Delete
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Delete Company</AlertDialogTitle>
                                <AlertDialogDescription>
                                  Are you sure you want to delete "{company.name}"? This action cannot be undone and will also delete all associated employees, jobs, and data.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction
                                  onClick={() => deleteCompanyMutation.mutate(company.id)}
                                  className="bg-red-600 hover:bg-red-700"
                                >
                                  Delete Company
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </>
                      )}
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/company-profile-setup.t.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Building2 } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/lib/auth";

const companyProfileSchema = z.object({
  name: z.string().min(2, "Company name must be at least 2 characters"),
  address: z.string().min(5, "Company address is required"),
  type: z.enum(["proprietorship", "partnership", "private_limited", "limited"], {
    required_error: "Please select company type",
  }),
  gstPan: z.string().min(10, "GST/PAN number is required"),
  pfNo: z.string().min(1, "PF number is required"),
  esicNo: z.string().min(1, "ESIC number is required"),
  cinNo: z.string().min(1, "CIN number is required"),
  authorizedPersonName: z.string().min(2, "Authorized person name is required"),
  authorizedPersonContact: z.string().min(10, "Contact number must be at least 10 digits"),
});

type CompanyProfileData = z.infer<typeof companyProfileSchema>;

export default function CompanyProfileSetup() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { user } = useAuth();

  const { data: company, isLoading: companyLoading } = useQuery({
    queryKey: [`/api/companies/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  const form = useForm<CompanyProfileData>({
    resolver: zodResolver(companyProfileSchema),
    defaultValues: {
      name: '',
      address: '',
      type: undefined,
      gstPan: '',
      pfNo: '',
      esicNo: '',
      cinNo: '',
      authorizedPersonName: '',
      authorizedPersonContact: '',
    },
  });

  const updateCompanyMutation = useMutation({
    mutationFn: async (data: CompanyProfileData) => {
      const response = await apiRequest('PUT', `/api/companies/${user?.companyId}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/companies'] });
      toast({ title: "Company profile completed successfully! Waiting for approval." });
      setLocation('/company-approval-pending');
    },
    onError: (error: any) => {
      toast({ 
        title: "Failed to update company profile", 
        description: error.message || "Please try again",
        variant: "destructive" 
      });
    },
  });

  const onSubmit = (data: CompanyProfileData) => {
    updateCompanyMutation.mutate(data);
  };

  const isLoading = updateCompanyMutation.isPending || companyLoading;

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-hr-background to-hr-background/80 p-4">
      <Card className="w-full max-w-4xl shadow-2xl border-0">
        <CardHeader className="text-center space-y-2 pb-6">
          <div className="flex items-center justify-center mb-4">
            <div className="bg-hr-primary/10 p-3 rounded-full">
              <Building2 className="h-8 w-8 text-hr-primary" />
            </div>
          </div>
          <CardTitle className="text-2xl font-bold text-hr-text-primary">
            Complete Company Profile
          </CardTitle>
          <CardDescription className="text-hr-text-secondary">
            Please provide your company details to continue using the HR management system
          </CardDescription>
        </CardHeader>

        <CardContent>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Company Basic Information */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-hr-text-primary border-b pb-2">
                Company Information
              </h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Company Name</Label>
                  <Input
                    id="name"
                    {...form.register("name")}
                    disabled={isLoading}
                    placeholder="Enter company name"
                  />
                  {form.formState.errors.name && (
                    <p className="text-sm text-red-600">{form.formState.errors.name.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="type">Company Type</Label>
                  <Select
                    value={form.watch("type")}
                    onValueChange={(value) => form.setValue("type", value as any)}
                    disabled={isLoading}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select company type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="proprietorship">Proprietorship</SelectItem>
                      <SelectItem value="partnership">Partnership</SelectItem>
                      <SelectItem value="private_limited">Private Limited</SelectItem>
                      <SelectItem value="limited">Limited</SelectItem>
                    </SelectContent>
                  </Select>
                  {form.formState.errors.type && (
                    <p className="text-sm text-red-600">{form.formState.errors.type.message}</p>
                  )}
                </div>
              </div>

              <div>
                <Label htmlFor="address">Company Address</Label>
                <Input
                  id="address"
                  {...form.register("address")}
                  disabled={isLoading}
                  placeholder="Enter complete company address"
                />
                {form.formState.errors.address && (
                  <p className="text-sm text-red-600">{form.formState.errors.address.message}</p>
                )}
              </div>
            </div>

            {/* Legal & Registration Details */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-hr-text-primary border-b pb-2">
                Legal & Registration Details
              </h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="gstPan">Company GST/PAN</Label>
                  <Input
                    id="gstPan"
                    {...form.register("gstPan")}
                    disabled={isLoading}
                    placeholder="GST or PAN number"
                  />
                  {form.formState.errors.gstPan && (
                    <p className="text-sm text-red-600">{form.formState.errors.gstPan.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="pfNo">Company PF No.</Label>
                  <Input
                    id="pfNo"
                    {...form.register("pfNo")}
                    disabled={isLoading}
                    placeholder="Provident Fund number"
                  />
                  {form.formState.errors.pfNo && (
                    <p className="text-sm text-red-600">{form.formState.errors.pfNo.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="esicNo">Company ESIC No.</Label>
                  <Input
                    id="esicNo"
                    {...form.register("esicNo")}
                    disabled={isLoading}
                    placeholder="ESIC registration number"
                  />
                  {form.formState.errors.esicNo && (
                    <p className="text-sm text-red-600">{form.formState.errors.esicNo.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="cinNo">Company CIN No.</Label>
                  <Input
                    id="cinNo"
                    {...form.register("cinNo")}
                    disabled={isLoading}
                    placeholder="Corporate Identification Number"
                  />
                  {form.formState.errors.cinNo && (
                    <p className="text-sm text-red-600">{form.formState.errors.cinNo.message}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Authorized Person Details */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-hr-text-primary border-b pb-2">
                Authorized Person Details
              </h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="authorizedPersonName">Authorized Person Name</Label>
                  <Input
                    id="authorizedPersonName"
                    {...form.register("authorizedPersonName")}
                    disabled={isLoading}
                    placeholder="Full name of authorized person"
                  />
                  {form.formState.errors.authorizedPersonName && (
                    <p className="text-sm text-red-600">{form.formState.errors.authorizedPersonName.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="authorizedPersonContact">Contact Number</Label>
                  <Input
                    id="authorizedPersonContact"
                    {...form.register("authorizedPersonContact")}
                    disabled={isLoading}
                    placeholder="Contact number"
                  />
                  {form.formState.errors.authorizedPersonContact && (
                    <p className="text-sm text-red-600">{form.formState.errors.authorizedPersonContact.message}</p>
                  )}
                </div>
              </div>
            </div>

            <Button
              type="submit"
              className="w-full bg-hr-primary hover:bg-hr-primary/90"
              disabled={isLoading}
            >
              {isLoading ? "Saving Company Profile..." : "Complete Setup & Continue"}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/company-settings.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { authService, apiRequestWithAuth, usePermission } from "@/lib/auth";
import { queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { 
  Building2, 
  Users, 
  MapPin, 
  Clock, 
  DollarSign, 
  Fingerprint, 
  Calendar, 
  FileText,
  Plus,
  Edit,
  Trash2,
  MoreVertical
} from "lucide-react";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";

interface Department {
  id: number;
  name: string;
  description: string;
  headOfDepartment: string;
  createdAt: Date;
}

interface Designation {
  id: number;
  title: string;
  departmentId: number;
  level: string;
  description: string;
}

interface Branch {
  id: number;
  name: string;
  address: string;
  city: string;
  state: string;
  zipCode: string;
  phone: string;
  manager: string;
}

interface Location {
  id: number;
  name: string;
  address: string;
  branchId: number;
  capacity: number;
}

interface CostCenter {
  id: number;
  code: string;
  name: string;
  description: string;
  budget: string;
  manager: string;
}

interface BiometricMachine {
  id: number;
  serialNumber: string;
  ipAddress: string;
  port: number;
  location: string;
  model: string;
  status: 'active' | 'inactive' | 'maintenance';
  lastSync: Date;
}

interface Holiday {
  id: number;
  name: string;
  date: Date;
  type: 'national' | 'regional' | 'company';
  description: string;
  mandatory: boolean;
}

interface LeavePolicy {
  id: number;
  name: string;
  type: 'annual' | 'sick' | 'maternity' | 'paternity' | 'personal' | 'emergency';
  allowedDays: number;
  carryForward: boolean;
  description: string;
}

export default function CompanySettings() {
  const { toast } = useToast();
  const user = authService.getUser();
  const companyId = user?.companyId;

  // State for active tab and dialogs
  const [activeTab, setActiveTab] = useState("departments");
  const [showDialog, setShowDialog] = useState(false);
  const [dialogType, setDialogType] = useState("");
  const [selectedItem, setSelectedItem] = useState<any>(null);

  // Form states
  const [formData, setFormData] = useState<any>({});

  // Permission check for company settings access
  const { hasPermission: canAccessSettings, loading: permissionLoading } = usePermission('company_settings');

  // Fetch company data
  const { data: company } = useQuery({
    queryKey: [`/api/companies/${companyId}`],
    enabled: !!companyId,
  });

  // Fetch departments
  const { data: departments = [] } = useQuery<Department[]>({
    queryKey: [`/api/departments/${companyId}`],
    enabled: !!companyId,
  });

  // Fetch all company settings data
  const { data: designations = [] } = useQuery<Designation[]>({
    queryKey: [`/api/designations/${companyId}`],
    enabled: !!companyId,
  });

  const { data: branches = [] } = useQuery<Branch[]>({
    queryKey: [`/api/branches/${companyId}`],
    enabled: !!companyId,
  });

  const { data: locations = [] } = useQuery<Location[]>({
    queryKey: [`/api/locations/${companyId}`],
    enabled: !!companyId,
  });

  const { data: costCenters = [] } = useQuery<CostCenter[]>({
    queryKey: [`/api/cost-centers/${companyId}`],
    enabled: !!companyId,
  });

  const { data: biometricMachines = [] } = useQuery<BiometricMachine[]>({
    queryKey: [`/api/biometric-machines/${companyId}`],
    enabled: !!companyId,
  });

  const { data: holidays = [] } = useQuery<Holiday[]>({
    queryKey: [`/api/holidays/${companyId}`],
    enabled: !!companyId,
  });

  const { data: leavePolicies = [] } = useQuery<LeavePolicy[]>({
    queryKey: [`/api/leave-policies/${companyId}`],
    enabled: !!companyId,
  });

  // Department mutation
  const departmentMutation = useMutation({
    mutationFn: async (data: any) => {
      const method = selectedItem ? 'PUT' : 'POST';
      const url = selectedItem ? `/api/departments/${selectedItem.id}` : '/api/departments';
      const response = await apiRequestWithAuth(method, url, { ...data, companyId });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/departments/${companyId}`] });
      setShowDialog(false);
      setFormData({});
      toast({
        title: "Success",
        description: `Department ${selectedItem ? 'updated' : 'created'} successfully`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save department",
        variant: "destructive",
      });
    },
  });

  // Designation mutation
  const designationMutation = useMutation({
    mutationFn: async (data: any) => {
      const method = selectedItem ? 'PUT' : 'POST';
      const url = selectedItem ? `/api/designations/${selectedItem.id}` : '/api/designations';
      const response = await apiRequestWithAuth(method, url, { ...data, companyId });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/designations/${companyId}`] });
      setShowDialog(false);
      setFormData({});
      toast({
        title: "Success",
        description: `Designation ${selectedItem ? 'updated' : 'created'} successfully`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save designation",
        variant: "destructive",
      });
    },
  });

  // Permission checks after all hooks
  if (permissionLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p>Checking permissions...</p>
        </div>
      </div>
    );
  }

  if (!canAccessSettings) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Building2 className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-red-600 mb-2">Access Denied</h2>
          <p className="text-gray-600 mb-4">You don't have permission to access company settings.</p>
          <p className="text-sm text-gray-500">Request 'company_settings' permission from your administrator.</p>
        </div>
      </div>
    );
  }

  const handleOpenDialog = (type: string, item?: any) => {
    setDialogType(type);
    setSelectedItem(item);
    setFormData(item || {});
    setShowDialog(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    switch (dialogType) {
      case 'department':
        departmentMutation.mutate(formData);
        break;
      case 'designation':
        designationMutation.mutate(formData);
        break;
      // Add other cases as needed
      default:
        toast({
          title: "Success",
          description: `${dialogType} ${selectedItem ? 'updated' : 'created'} successfully`,
        });
        setShowDialog(false);
    }
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleDateString();
  };

  const renderDepartments = () => (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <div>
          <CardTitle>Departments</CardTitle>
          <CardDescription>Manage organizational departments</CardDescription>
        </div>
        <Button onClick={() => handleOpenDialog('department')} className="bg-hr-primary hover:bg-hr-primary/90">
          <Plus className="mr-2 h-4 w-4" />
          Add Department
        </Button>
      </CardHeader>
      <CardContent>
        {departments.length === 0 ? (
          <div className="text-center py-8">
            <Building2 className="mx-auto h-12 w-12 text-gray-400" />
            <p className="mt-4 text-gray-600">No departments found</p>
            <Button onClick={() => handleOpenDialog('department')} className="mt-4">
              Create First Department
            </Button>
          </div>
        ) : (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Department Name</TableHead>
                <TableHead>Description</TableHead>
                <TableHead>Head of Department</TableHead>
                <TableHead>Created</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
