                onClick={() => setShowDialog(false)}
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="bg-hr-primary hover:bg-hr-primary/90"
                disabled={departmentMutation.isPending}
              >
                {departmentMutation.isPending ? 'Saving...' : (selectedItem ? 'Update' : 'Create')}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/compliances-data.tsx.content:
import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { FileSpreadsheet, Filter, Calendar, Plus, Download } from 'lucide-react';
import { useAuth } from '@/lib/auth';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

export default function CompliancesData() {
  const { user } = useAuth();
  const [selectedProject, setSelectedProject] = useState<string>('');
  const [selectedMonth, setSelectedMonth] = useState<string>('');
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: projects = [] } = useQuery({
    queryKey: ['/api/clients', user?.companyId],
    enabled: !!user?.companyId,
  });

  const { data: compliancesData = [] } = useQuery({
    queryKey: [`/api/compliances-data/${user?.companyId}/${selectedProject}/${selectedMonth}`],
    enabled: !!user?.companyId && !!selectedProject && !!selectedMonth,
  });

  // Generate sample data mutation
  const generateSampleDataMutation = useMutation({
    mutationFn: async () => {
      if (!selectedProject || !selectedMonth) return;

      // Get compliance setups for sample data generation
      const setupResponse = await apiRequest('GET', `/api/compliance-setups/${user?.companyId}`);
      const setups = await setupResponse.json();

      if (setups.length === 0) {
        throw new Error('No employee compliance setups found. Please set up employees first.');
      }

      // Generate compliance data for each setup
      const sampleData = setups.map((setup: any) => ({
        companyId: user?.companyId,
        projectId: parseInt(selectedProject),
        employeeId: setup.employeeId,
        month: selectedMonth,
        actualPayable: (Math.random() * 50000 + 30000).toFixed(2),
        compliancesGross: (Math.random() * 45000 + 28000).toFixed(2),
        compliancesAttendance: Math.floor(Math.random() * 8) + 20, // 20-27 days
        compliancesOT: Math.floor(Math.random() * 20), // 0-20 hours
        compliancesGrossEarn: (Math.random() * 48000 + 29000).toFixed(2),
        pfDeduction: (Math.random() * 3000 + 1500).toFixed(2),
        esicDeduction: (Math.random() * 1500 + 500).toFixed(2),
        netPayableAmount: (Math.random() * 42000 + 25000).toFixed(2),
        differenceAmount: ((Math.random() - 0.5) * 5000).toFixed(2),
      }));

      // Create all compliance data records
      for (const data of sampleData) {
        await apiRequest('POST', '/api/compliances-data', data);
      }

      return sampleData;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ 
        queryKey: [`/api/compliances-data/${user?.companyId}/${selectedProject}/${selectedMonth}`] 
      });
      toast({
        title: "Success",
        description: "Sample compliance data generated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to generate sample data",
        variant: "destructive",
      });
    },
  });

  // Generate month options for current and previous months
  const getMonthOptions = () => {
    const months = [];
    const currentDate = new Date();

    for (let i = 0; i < 12; i++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
      const monthYear = date.toISOString().slice(0, 7); // YYYY-MM format
      const monthName = date.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
      months.push({ value: monthYear, label: monthName });
    }

    return months;
  };

  const monthOptions = getMonthOptions();

  const formatCurrency = (amount: number | string) => {
    return `‚Çπ${Number(amount).toLocaleString('en-IN')}`;
  };

  const handleExportToExcel = () => {
    const token = localStorage.getItem('token');
    const url = `/api/compliances/export/${user?.companyId}`;

    // Create a temporary link with auth header
    fetch(url, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    .then(response => response.blob())
    .then(blob => {
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `compliances_${new Date().toISOString().split('T')[0]}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    })
    .catch(error => {
      toast({
        title: "Error",
        description: "Failed to export compliance data",
        variant: "destructive",
      });
    });
  };

  return (
    <div className="space-y-6 p-6">

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Filter className="w-5 h-5 mr-2 text-blue-600" />
            Filters
          </CardTitle>
          <CardDescription>
            Select project and month to view compliance data
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium mb-2 block">Project Selection</label>
              <Select value={selectedProject} onValueChange={setSelectedProject}>
                <SelectTrigger>
                  <SelectValue placeholder="Select a project" />
                </SelectTrigger>
                <SelectContent>
                  {projects.map((project: any) => (
                    <SelectItem key={project.id} value={project.id.toString()}>
                      {project.projectName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="text-sm font-medium mb-2 block">Month Selection</label>
              <Select value={selectedMonth} onValueChange={setSelectedMonth}>
                <SelectTrigger>
                  <SelectValue placeholder="Select month" />
                </SelectTrigger>
                <SelectContent>
                  {monthOptions.map((month) => (
                    <SelectItem key={month.value} value={month.value}>
                      {month.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Compliances Data Table */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center">
                <FileSpreadsheet className="w-5 h-5 mr-2 text-green-600" />
                Employee Compliances Data
              </CardTitle>
              <CardDescription>
                Detailed compliance breakdown for selected project and month
              </CardDescription>
            </div>
            <Button 
              onClick={handleExportToExcel} 
              variant="outline"
              className="flex items-center gap-2"
            >
              <Download className="h-4 w-4" />
              Export to Excel
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {!selectedProject || !selectedMonth ? (
            <div className="text-center py-12 text-gray-500">
              <Calendar className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p className="text-lg font-medium mb-2">Select Project and Month</p>
              <p>Please select both project and month to view compliance data.</p>
            </div>
          ) : compliancesData.length === 0 ? (
            <div className="text-center py-12 text-gray-500">
              <FileSpreadsheet className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p className="text-lg font-medium mb-2">No Data Found</p>
              <p className="mb-4">No compliance data available for the selected project and month.</p>
              <div className="space-y-2">
                <p className="text-sm text-blue-600 mb-3">
                  üí° <strong>Automatic Generation:</strong> Compliance data is automatically created when employees are assigned to projects
                </p>
                <Button 
                  onClick={() => generateSampleDataMutation.mutate()}
                  disabled={generateSampleDataMutation.isPending}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  {generateSampleDataMutation.isPending ? "Generating..." : "Generate Sample Data"}
                </Button>
              </div>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead className="w-12">S.N.</TableHead>
                    <TableHead>Employee Name</TableHead>
                    <TableHead>Designation</TableHead>
                    <TableHead className="text-right">Actual Payable</TableHead>
                    <TableHead className="text-right">Compliances Gross</TableHead>
                    <TableHead className="text-center">Compliances Attendance</TableHead>
                    <TableHead className="text-right">Compliances OT</TableHead>
                    <TableHead className="text-right">Compliances Gross Ern</TableHead>
                    <TableHead className="text-right">PF Deduction</TableHead>
                    <TableHead className="text-right">ESIC Deduction</TableHead>
                    <TableHead className="text-right">Net Payable Amount</TableHead>
                    <TableHead className="text-right">Difference Amount</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {compliancesData.map((item: any, index: number) => (
                    <TableRow key={item.id}>
                      <TableCell className="font-medium">{index + 1}</TableCell>
                      <TableCell>
                        <div>
                          <p className="font-medium">{item.employee?.firstName} {item.employee?.lastName}</p>
                          <p className="text-sm text-gray-500">{item.employee?.employeeId}</p>
                        </div>
                      </TableCell>
                      <TableCell>{item.employee?.designation || 'Not Set'}</TableCell>
                      <TableCell className="text-right font-medium">
                        {formatCurrency(item.actualPayable || 0)}
                      </TableCell>
                      <TableCell className="text-right">
                        {formatCurrency(item.compliancesGross || 0)}
                      </TableCell>
                      <TableCell className="text-center">
                        <Badge variant={item.compliancesAttendance >= 22 ? 'default' : 'secondary'}>
                          {item.compliancesAttendance || 0} days
                        </Badge>
                      </TableCell>
                      <TableCell className="text-right">
                        {item.compliancesOT || 0} hrs
                      </TableCell>
                      <TableCell className="text-right">
                        {formatCurrency(item.compliancesGrossEarn || 0)}
                      </TableCell>
                      <TableCell className="text-right text-red-600">
                        -{formatCurrency(item.pfDeduction || 0)}
                      </TableCell>
                      <TableCell className="text-right text-red-600">
                        -{formatCurrency(item.esicDeduction || 0)}
                      </TableCell>
                      <TableCell className="text-right font-semibold text-green-600">
                        {formatCurrency(item.netPayableAmount || 0)}
                      </TableCell>
                      <TableCell className="text-right">
                        <Badge variant={Number(item.differenceAmount || 0) >= 0 ? 'default' : 'destructive'}>
                          {formatCurrency(item.differenceAmount || 0)}
                        </Badge>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Summary Card */}
      {compliancesData.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="text-center">
                <p className="text-sm text-gray-600">Total Employees</p>
                <p className="text-2xl font-bold text-blue-600">{compliancesData.length}</p>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="text-center">
                <p className="text-sm text-gray-600">Total Payable</p>
                <p className="text-2xl font-bold text-green-600">
                  {formatCurrency(
                    compliancesData.reduce((sum: number, item: any) => 
                      sum + Number(item.actualPayable || 0), 0
                    )
                  )}
                </p>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="text-center">
                <p className="text-sm text-gray-600">Total PF Deduction</p>
                <p className="text-2xl font-bold text-red-600">
                  {formatCurrency(
                    compliancesData.reduce((sum: number, item: any) => 
                      sum + Number(item.pfDeduction || 0), 0
                    )
                  )}
                </p>
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="text-center">
                <p className="text-sm text-gray-600">Total ESIC Deduction</p>
                <p className="text-2xl font-bold text-red-600">
                  {formatCurrency(
                    compliancesData.reduce((sum: number, item: any) => 
                      sum + Number(item.esicDeduction || 0), 0
                    )
                  )}
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

client/src/pages/daily-log.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Edit, Trash2, Plus } from "lucide-react";
import { authService } from "@/lib/auth";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface DailyAttendanceRecord {
  id: number;
  date: string;
  day: string;
  inTime: string;
  outTime: string;
  workingHours: number;
  otHours: number;
  status: 'Present' | 'Absent' | 'Late' | 'Half Day' | 'Leave';
  source: 'Biometric' | 'Manual' | 'Self';
}

export default function DailyLogPage() {
  const [, params] = useRoute("/admin/attendance/daily-log/:employeeId/:year/:month");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const user = authService.getUser();

  const employeeId = params?.employeeId ? parseInt(params.employeeId) : 0;
  const year = params?.year ? parseInt(params.year) : new Date().getFullYear();
  const month = params?.month ? parseInt(params.month) : new Date().getMonth() + 1;

  // Dialog states
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingRecord, setEditingRecord] = useState<DailyAttendanceRecord | null>(null);

  // Form states
  const [formData, setFormData] = useState({
    date: '',
    inTime: '',
    outTime: '',
    status: 'Present' as DailyAttendanceRecord['status'],
    source: 'Manual' as DailyAttendanceRecord['source']
  });

  // Get employee details
  const { data: employee } = useQuery({
    queryKey: [`/api/employees/${user?.companyId}/${employeeId}`],
    enabled: !!user?.companyId && !!employeeId,
  });

  // Get daily attendance records
  const { data: dailyRecords = [], isLoading } = useQuery<DailyAttendanceRecord[]>({
    queryKey: [`/api/attendance/daily-log/${employeeId}/${year}/${month}`],
    enabled: !!employeeId,
  });

  const getMonthName = (monthNum: number) => {
    const months = [
      'January', 'February', 'March', 'April', 'May', 'June',
      'July', 'August', 'September', 'October', 'November', 'December'
    ];
    return months[monthNum - 1];
  };

  const calculateWorkingHours = (inTime: string, outTime: string): number => {
    if (!inTime || !outTime) return 0;
    const inDate = new Date(`1970-01-01T${inTime}`);
    const outDate = new Date(`1970-01-01T${outTime}`);
    return (outDate.getTime() - inDate.getTime()) / (1000 * 60 * 60);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Present': return 'bg-green-100 text-green-800';
      case 'Absent': return 'bg-red-100 text-red-800';
      case 'Late': return 'bg-yellow-100 text-yellow-800';
      case 'Half Day': return 'bg-blue-100 text-blue-800';
      case 'Leave': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getSourceColor = (source: string) => {
    switch (source) {
      case 'Biometric': return 'bg-indigo-100 text-indigo-800';
      case 'Manual': return 'bg-orange-100 text-orange-800';
      case 'Self': return 'bg-cyan-100 text-cyan-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  // Add record mutation
  const addRecordMutation = useMutation({
    mutationFn: async (data: any) => {
      const token = authService.getToken();
      if (!token) throw new Error('Authentication required');

      const response = await fetch('/api/attendance/daily-record', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          employeeId,
          ...data,
          workingHours: calculateWorkingHours(data.inTime, data.outTime),
          otHours: Math.max(0, calculateWorkingHours(data.inTime, data.outTime) - 8)
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to add record');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Daily record added successfully" });
      setIsAddDialogOpen(false);
      resetForm();
      queryClient.invalidateQueries({ queryKey: [`/api/attendance/daily-log/${employeeId}/${year}/${month}`] });
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    }
  });

  // Edit record mutation
  const editRecordMutation = useMutation({
    mutationFn: async (data: any) => {
      const token = authService.getToken();
      if (!token) throw new Error('Authentication required');

      const response = await fetch(`/api/attendance/daily-record/${editingRecord?.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ...data,
          workingHours: calculateWorkingHours(data.inTime, data.outTime),
          otHours: Math.max(0, calculateWorkingHours(data.inTime, data.outTime) - 8)
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to update record');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Daily record updated successfully" });
      setIsEditDialogOpen(false);
      setEditingRecord(null);
      resetForm();
      queryClient.invalidateQueries({ queryKey: [`/api/attendance/daily-log/${employeeId}/${year}/${month}`] });
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    }
  });

  // Delete record mutation
  const deleteRecordMutation = useMutation({
    mutationFn: async (recordId: number) => {
      const token = authService.getToken();
      if (!token) throw new Error('Authentication required');

      const response = await fetch(`/api/attendance/daily-record/${recordId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to delete record');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Daily record deleted successfully" });
      queryClient.invalidateQueries({ queryKey: [`/api/attendance/daily-log/${employeeId}/${year}/${month}`] });
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    }
  });

  const resetForm = () => {
    setFormData({
      date: '',
      inTime: '',
      outTime: '',
      status: 'Present',
      source: 'Manual'
    });
  };

  const handleEdit = (record: DailyAttendanceRecord) => {
    setEditingRecord(record);
    setFormData({
      date: record.date,
      inTime: record.inTime,
      outTime: record.outTime,
      status: record.status,
      source: record.source
    });
    setIsEditDialogOpen(true);
  };

  const handleDelete = (record: DailyAttendanceRecord) => {
    if (window.confirm(`Are you sure you want to delete the record for ${record.date}?`)) {
      deleteRecordMutation.mutate(record.id);
    }
  };

  const submitForm = () => {
    if (!formData.date || !formData.inTime) {
      toast({ title: "Error", description: "Date and In Time are required", variant: "destructive" });
      return;
    }

    if (editingRecord) {
      editRecordMutation.mutate(formData);
    } else {
      addRecordMutation.mutate(formData);
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="text-lg">Loading daily attendance records...</div>
        </div>
      </div>
    );
  }

  const employeeName = employee ? `${employee[0]?.firstName} ${employee[0]?.lastName}` : 'Employee';

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button
            variant="outline"
            onClick={() => setLocation('/admin/attendance')}
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Attendance
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Daily Attendance Log</h1>
            <p className="text-gray-600">
              {employeeName} - {getMonthName(month)} {year}
            </p>
          </div>
        </div>
        <Button onClick={() => setIsAddDialogOpen(true)}>
          <Plus className="h-4 w-4 mr-2" />
          Add Record
        </Button>
      </div>

      {/* Daily Records Table */}
      <Card>
        <CardHeader>
          <CardTitle>Daily Attendance Records</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Date</TableHead>
                <TableHead>Day</TableHead>
                <TableHead>In Time</TableHead>
                <TableHead>Out Time</TableHead>
                <TableHead>Working Hours</TableHead>
                <TableHead>OT Hours</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Source</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {dailyRecords.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={9} className="text-center py-8 text-gray-500">
                    No daily attendance records found for this period
                  </TableCell>
                </TableRow>
              ) : (
                dailyRecords.map((record) => (
                  <TableRow key={record.id}>
                    <TableCell className="font-medium">{record.date}</TableCell>
                    <TableCell>{record.day}</TableCell>
                    <TableCell>{record.inTime || '-'}</TableCell>
                    <TableCell>{record.outTime || '-'}</TableCell>
                    <TableCell>{record.workingHours.toFixed(1)}h</TableCell>
                    <TableCell>{record.otHours.toFixed(1)}h</TableCell>
                    <TableCell>
                      <Badge className={getStatusColor(record.status)}>
                        {record.status}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <Badge className={getSourceColor(record.source)}>
                        {record.source}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEdit(record)}
                        >
                          <Edit className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          className="text-red-600 hover:text-red-700"
                          onClick={() => handleDelete(record)}
                        >
                          <Trash2 className="h-3 w-3" />
