import { useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { FileText, CheckCircle, XCircle, Clock } from 'lucide-react';

export default function PermissionRequestSimple() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [permissionType, setPermissionType] = useState('');
  const [reason, setReason] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch permission requests
  const { data: permissionRequests = [], isLoading } = useQuery({
    queryKey: ['/api/permission-requests'],
    queryFn: async () => {
      const response = await fetch('/api/permission-requests', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch');
      return response.json();
    },
  });

  // Create permission request mutation
  const createRequestMutation = useMutation({
    mutationFn: async (data: { permissionType: string; reason: string }) => {
      const response = await fetch('/api/permission-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error('Failed to create permission request');
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/permission-requests'] });
      setIsDialogOpen(false);
      setPermissionType('');
      setReason('');
      toast({ title: 'Success', description: 'Permission request submitted successfully!' });
    },
    onError: () => {
      toast({ title: 'Error', description: 'Failed to create permission request', variant: 'destructive' });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!permissionType || !reason) {
      toast({ title: "Error", description: "Please fill all fields", variant: "destructive" });
      return;
    }

    createRequestMutation.mutate({ permissionType, reason });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'approved':
        return <Badge className="bg-green-100 text-green-800"><CheckCircle className="w-3 h-3 mr-1" />Approved</Badge>;
      case 'rejected':
        return <Badge className="bg-red-100 text-red-800"><XCircle className="w-3 h-3 mr-1" />Rejected</Badge>;
      case 'pending':
        return <Badge className="bg-yellow-100 text-yellow-800"><Clock className="w-3 h-3 mr-1" />Pending</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Permission Requests</h1>
          <p className="text-muted-foreground">
            Request additional permissions from your administrator
          </p>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button 
              className="bg-blue-600 hover:bg-blue-700 text-white"
              onClick={() => setIsDialogOpen(true)}
            >
              <FileText className="w-4 h-4 mr-2" />
              Request New Permission
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Request Permission</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="permissionType">Permission Type</Label>
                <Select value={permissionType} onValueChange={setPermissionType} required>
                  <SelectTrigger>
                    <SelectValue placeholder="Select permission type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="employee_edit">Employee Edit - Edit employee information</SelectItem>
                    <SelectItem value="employee_create">Employee Create - Create new employee records</SelectItem>
                    <SelectItem value="employee_delete">Employee Delete - Delete employee records</SelectItem>
                    <SelectItem value="job_create">Job Create - Create new job postings</SelectItem>
                    <SelectItem value="job_edit">Job Edit - Edit existing job postings</SelectItem>
                    <SelectItem value="job_delete">Job Delete - Delete job postings</SelectItem>
                    <SelectItem value="department_create">Department Create - Create new departments</SelectItem>
                    <SelectItem value="department_edit">Department Edit - Edit department information</SelectItem>
                    <SelectItem value="company_settings">Company Settings - Access company settings</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="reason">Reason for Request</Label>
                <Input 
                  id="reason" 
                  value={reason}
                  onChange={(e) => setReason(e.target.value)}
                  required 
                  placeholder="Explain why you need this permission"
                />
              </div>
              <Button 
                type="submit" 
                disabled={createRequestMutation.isPending}
                className="w-full bg-blue-600 hover:bg-blue-700"
              >
                {createRequestMutation.isPending ? 'Submitting...' : 'Submit Permission Request'}
              </Button>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>My Permission Requests</CardTitle>
          <CardDescription>
            Track your submitted permission requests and their status
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div>Loading requests...</div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Permission Type</TableHead>
                  <TableHead>Reason</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Created</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {permissionRequests.map((request: any) => (
                  <TableRow key={request.id}>
                    <TableCell className="font-medium">{request.permissionType}</TableCell>
                    <TableCell>{request.reason}</TableCell>
                    <TableCell>{getStatusBadge(request.status)}</TableCell>
                    <TableCell>{new Date(request.createdAt).toLocaleDateString()}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/recruitment-dashboard.t.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Link } from "wouter";
import { 
  ArrowLeft, Plus, Eye, Calendar, CheckCircle, XCircle, Search, Download, 
  Clock, User, Briefcase, Phone, Mail, MapPin, FileText, Star
} from "lucide-react";
import { useAuth } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useState } from "react";

export default function RecruitmentDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [selectedStatus, setSelectedStatus] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedApplications, setSelectedApplications] = useState<number[]>([]);

  const { data: jobs = [] } = useQuery({
    queryKey: [`/api/jobs/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  const { data: jobApplications = [] } = useQuery({
    queryKey: [`/api/job-applications/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  const { data: interviews = [] } = useQuery({
    queryKey: [`/api/interviews/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  const { data: jobOffers = [] } = useQuery({
    queryKey: [`/api/job-offers/${user?.companyId}`],
    enabled: !!user?.companyId,
  });

  const updateApplicationMutation = useMutation({
    mutationFn: async ({ id, status }: { id: number; status: string }) => {
      return await apiRequest('PUT', `/api/job-applications/${id}/status`, { status });
    },
    onSuccess: () => {
      toast({ title: "Application status updated successfully!" });
      queryClient.invalidateQueries({ queryKey: [`/api/job-applications/${user?.companyId}`] });
    },
    onError: () => {
      toast({ title: "Failed to update application status", variant: "destructive" });
    },
  });

  const scheduleInterviewMutation = useMutation({
    mutationFn: async (interviewData: any) => {
      return await apiRequest('POST', '/api/interviews', interviewData);
    },
    onSuccess: () => {
      toast({ title: "Interview scheduled successfully!" });
      queryClient.invalidateQueries({ queryKey: [`/api/interviews/${user?.companyId}`] });
    },
    onError: () => {
      toast({ title: "Failed to schedule interview", variant: "destructive" });
    },
  });

  const createOfferMutation = useMutation({
    mutationFn: async (offerData: any) => {
      return await apiRequest('POST', '/api/job-offers', offerData);
    },
    onSuccess: () => {
      toast({ title: "Job offer created successfully!" });
      queryClient.invalidateQueries({ queryKey: [`/api/job-offers/${user?.companyId}`] });
    },
    onError: () => {
      toast({ title: "Failed to create job offer", variant: "destructive" });
    },
  });

  const activeJobs = jobs.filter((job: any) => job.status === 'active');
  const newApplications = jobApplications.filter((app: any) => app.status === 'applied');
  const underReview = jobApplications.filter((app: any) => app.status === 'under_review');
  const todayInterviews = interviews.filter((int: any) => 
    new Date(int.scheduledAt).toDateString() === new Date().toDateString()
  );

  // Candidate search functionality
  const handleCandidateSearch = () => {
    if (!searchQuery.trim()) {
      toast({ title: "Please enter search criteria", variant: "destructive" });
      return;
    }

    const filteredCandidates = jobApplications.filter((app: any) => 
      app.applicantName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      app.applicantEmail.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (app.coverLetter && app.coverLetter.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    toast({ 
      title: `Found ${filteredCandidates.length} candidates`, 
      description: `Matching search criteria: "${searchQuery}"` 
    });
  };

  const handleDownloadResumes = () => {
    const candidatesWithResumes = jobApplications.filter((app: any) => app.resume);
    if (candidatesWithResumes.length === 0) {
      toast({ title: "No resumes available to download", variant: "destructive" });
      return;
    }

    toast({ 
      title: `Downloading ${candidatesWithResumes.length} resumes`, 
      description: "Resume files will be prepared for download" 
    });
  };

  // Bulk actions functionality
  const handleBulkAccept = () => {
    if (selectedApplications.length === 0) {
      toast({ title: "No applications selected", variant: "destructive" });
      return;
    }

    selectedApplications.forEach(id => {
      updateApplicationMutation.mutate({ id, status: 'accepted' });
    });

    setSelectedApplications([]);
    toast({ title: `Accepted ${selectedApplications.length} applications` });
  };

  const handleBulkReject = () => {
    if (selectedApplications.length === 0) {
      toast({ title: "No applications selected", variant: "destructive" });
      return;
    }

    selectedApplications.forEach(id => {
      updateApplicationMutation.mutate({ id, status: 'rejected' });
    });

    setSelectedApplications([]);
    toast({ title: `Rejected ${selectedApplications.length} applications` });
  };

  const filteredApplications = selectedStatus === 'all' 
    ? jobApplications 
    : jobApplications.filter((app: any) => app.status === selectedStatus);

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-4">
        <Link href="/admin">
          <Button variant="outline" size="sm">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Dashboard
          </Button>
        </Link>
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Recruitment Center</h1>
          <p className="text-muted-foreground">
            Complete recruitment management system
          </p>
        </div>
      </div>

      {/* Quick Stats */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Jobs</CardTitle>
            <Briefcase className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-hr-primary">{activeJobs.length}</div>
            <p className="text-xs text-muted-foreground">
              {jobs.filter((j: any) => j.status === 'draft').length} in draft
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">New Applications</CardTitle>
            <User className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-600">{newApplications.length}</div>
            <p className="text-xs text-muted-foreground">
              {underReview.length} under review
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Today's Interviews</CardTitle>
            <Calendar className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{todayInterviews.length}</div>
            <p className="text-xs text-muted-foreground">
              {interviews.filter((int: any) => int.status === 'scheduled').length} scheduled
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Offers</CardTitle>
            <CheckCircle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-yellow-600">{jobOffers.filter((offer: any) => offer.status === 'pending').length}</div>
            <p className="text-xs text-muted-foreground">
              {jobOffers.filter((offer: any) => offer.status === 'accepted').length} accepted
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Main Actions */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Plus className="h-5 w-5 text-hr-primary" />
              Post New Job
            </CardTitle>
            <CardDescription>Create and publish job openings</CardDescription>
          </CardHeader>
          <CardContent>
            <Link href="/jobs">
              <Button className="w-full bg-hr-primary hover:bg-hr-primary/90">
                <Plus className="mr-2 h-4 w-4" />
                Create Job Posting
              </Button>
            </Link>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Search className="h-5 w-5 text-blue-600" />
              Candidate Search
            </CardTitle>
            <CardDescription>Find and download candidate resumes</CardDescription>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="flex gap-2">
              <Input 
                placeholder="Search by name, email, skills..." 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleCandidateSearch()}
              />
              <Button size="sm" variant="outline" onClick={handleCandidateSearch}>
                <Search className="h-4 w-4" />
              </Button>
            </div>
            <Button className="w-full" variant="outline" onClick={handleDownloadResumes}>
              <Download className="mr-2 h-4 w-4" />
              Download All Resumes ({jobApplications.filter((app: any) => app.resume).length})
            </Button>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Clock className="h-5 w-5 text-green-600" />
              Quick Actions
            </CardTitle>
            <CardDescription>Bulk operations and shortcuts</CardDescription>
          </CardHeader>
          <CardContent className="space-y-2">
            <Button 
              className="w-full justify-start" 
              size="sm" 
              variant="outline"
              onClick={handleBulkAccept}
              disabled={selectedApplications.length === 0}
            >
              <CheckCircle className="mr-2 h-4 w-4" />
              Bulk Accept ({selectedApplications.length} selected)
            </Button>
            <Button 
              className="w-full justify-start" 
              size="sm" 
              variant="outline"
              onClick={handleBulkReject}
              disabled={selectedApplications.length === 0}
            >
              <XCircle className="mr-2 h-4 w-4" />
              Bulk Reject ({selectedApplications.length} selected)
            </Button>
            <Link href="/jobs">
              <Button className="w-full justify-start" size="sm" variant="outline">
                <Briefcase className="mr-2 h-4 w-4" />
                Manage Job Postings
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>

      {/* Recruitment Pipeline */}
      <Card>
        <CardHeader>
          <CardTitle>Recruitment Pipeline</CardTitle>
          <CardDescription>Track applications through the hiring process</CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs value={selectedStatus} onValueChange={setSelectedStatus}>
            <TabsList className="grid w-full grid-cols-6">
              <TabsTrigger value="all">All ({jobApplications.length})</TabsTrigger>
              <TabsTrigger value="applied">New ({newApplications.length})</TabsTrigger>
              <TabsTrigger value="under_review">Review ({underReview.length})</TabsTrigger>
              <TabsTrigger value="interviewed">Interviewed</TabsTrigger>
              <TabsTrigger value="offered">Offered</TabsTrigger>
              <TabsTrigger value="hired">Hired</TabsTrigger>
            </TabsList>

            <TabsContent value={selectedStatus} className="mt-6">
              <div className="space-y-4">
                {filteredApplications.length === 0 ? (
                  <div className="text-center py-8 text-muted-foreground">
                    No applications found
                  </div>
                ) : (
                  filteredApplications.map((application: any) => (
                    <Card key={application.id} className="p-4">
                      <div className="flex items-center justify-between">
                        <div className="space-y-2">
                          <div className="flex items-center gap-3">
                            <div className="h-10 w-10 rounded-full bg-hr-primary/10 flex items-center justify-center">
                              <User className="h-5 w-5 text-hr-primary" />
                            </div>
                            <div>
                              <h3 className="font-semibold">{application.applicantName}</h3>
                              <p className="text-sm text-muted-foreground">{application.applicantEmail}</p>
                            </div>
                            <Badge variant={
                              application.status === 'applied' ? 'default' :
                              application.status === 'under_review' ? 'secondary' :
                              application.status === 'hired' ? 'default' : 'outline'
                            }>
                              {application.status.replace('_', ' ')}
                            </Badge>
                          </div>
                          <div className="flex items-center gap-4 text-sm text-muted-foreground">
                            <span className="flex items-center gap-1">
                              <Briefcase className="h-3 w-3" />
                              {jobs.find((job: any) => job.id === application.jobId)?.title}
                            </span>
                            <span className="flex items-center gap-1">
                              <Calendar className="h-3 w-3" />
                              Applied {new Date(application.appliedAt).toLocaleDateString()}
                            </span>
                          </div>
                        </div>

                        <div className="flex items-center gap-2">
                          <Dialog>
                            <DialogTrigger asChild>
                              <Button size="sm" variant="outline">
                                <Eye className="mr-1 h-3 w-3" />
                                View
                              </Button>
                            </DialogTrigger>
                            <DialogContent className="max-w-2xl">
                              <DialogHeader>
                                <DialogTitle>Application Details</DialogTitle>
                                <DialogDescription>
                                  {application.applicantName} - {jobs.find((job: any) => job.id === application.jobId)?.title}
                                </DialogDescription>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div>
                                  <Label>Cover Letter</Label>
                                  <p className="text-sm mt-1">{application.coverLetter || "No cover letter provided"}</p>
                                </div>
                                <div>
                                  <Label>Resume</Label>
                                  <Button size="sm" variant="outline" className="mt-1">
                                    <Download className="mr-1 h-3 w-3" />
                                    Download Resume
                                  </Button>
                                </div>
                              </div>
                            </DialogContent>
                          </Dialog>

                          <Select 
                            value={application.status} 
                            onValueChange={(status) => updateApplicationMutation.mutate({ id: application.id, status })}
                          >
                            <SelectTrigger className="w-32">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="applied">Applied</SelectItem>
                              <SelectItem value="under_review">Under Review</SelectItem>
                              <SelectItem value="interviewed">Interviewed</SelectItem>
                              <SelectItem value="offered">Offered</SelectItem>
                              <SelectItem value="hired">Hired</SelectItem>
                              <SelectItem value="rejected">Rejected</SelectItem>
                            </SelectContent>
                          </Select>

                          <Dialog>
                            <DialogTrigger asChild>
                              <Button size="sm">
                                <Calendar className="mr-1 h-3 w-3" />
                                Schedule
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Schedule Interview</DialogTitle>
                                <DialogDescription>
                                  Schedule an interview for {application.applicantName}
                                </DialogDescription>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div>
                                  <Label>Date & Time</Label>
                                  <Input type="datetime-local" />
                                </div>
                                <div>
                                  <Label>Duration (minutes)</Label>
                                  <Input type="number" defaultValue="60" />
                                </div>
                                <div>
                                  <Label>Location/Link</Label>
                                  <Input placeholder="Meeting room or video link" />
                                </div>
                              </div>
                              <DialogFooter>
                                <Button>Schedule Interview</Button>
                              </DialogFooter>
                            </DialogContent>
                          </Dialog>
                        </div>
                      </div>
                    </Card>
                  ))
                )}
              </div>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/signup.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Building2, User, ArrowLeft } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { authService } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";

const adminSignupSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

const employeeSignupSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

type AdminSignupData = z.infer<typeof adminSignupSchema>;
type EmployeeSignupData = z.infer<typeof employeeSignupSchema>;

export default function SignupPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("admin");

  // No need to fetch companies or departments for employee signup

  const adminForm = useForm<AdminSignupData>({
    resolver: zodResolver(adminSignupSchema),
  });

  const employeeForm = useForm<EmployeeSignupData>({
    resolver: zodResolver(employeeSignupSchema),
  });

  const adminSignupMutation = useMutation({
    mutationFn: async (data: AdminSignupData) => {
      // Create admin user with simplified data
      const userResponse = await apiRequest('POST', '/api/users/admin-signup', {
        name: data.name,
        email: data.email,
        password: data.password,
      });
      return userResponse.json();
    },
    onSuccess: async (data) => {
      toast({ title: "Account created successfully! Please log in." });
      setLocation('/login');
    },
    onError: (error: any) => {
      toast({ 
        title: "Signup failed", 
        description: error.message || "Please try again",
        variant: "destructive" 
      });
    },
  });

  const employeeSignupMutation = useMutation({
    mutationFn: async (data: EmployeeSignupData) => {
      // Create employee user with simplified data
      const userResponse = await apiRequest('POST', '/api/users/employee-signup', {
        name: data.name,
        email: data.email,
        password: data.password,
      });

      if (!userResponse.ok) {
        const errorData = await userResponse.json().catch(() => ({ message: 'Unknown error' }));
        throw new Error(errorData.message || 'Signup failed');
      }

      return await userResponse.json();
    },
    onSuccess: () => {
      toast({ title: "Employee account created successfully! You can now browse and apply for jobs." });
      setLocation('/login');
    },
    onError: (error: any) => {
      console.error('Frontend: Signup mutation error:', error);
      toast({ 
        title: "Signup failed", 
        description: error.message || "Please try again",
        variant: "destructive" 
      });
    },
  });

  const onAdminSubmit = (data: AdminSignupData) => {
    adminSignupMutation.mutate(data);
  };

  const onEmployeeSubmit = (data: EmployeeSignupData) => {
    employeeSignupMutation.mutate(data);
  };

  const isLoading = adminSignupMutation.isPending || employeeSignupMutation.isPending;

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-hr-background to-hr-background/80 p-4">
      <Card className="w-full max-w-2xl shadow-2xl border-0">
        <CardHeader className="text-center space-y-2 pb-6">
          <div className="flex items-center justify-center mb-4">
            <div className="bg-hr-primary/10 p-3 rounded-full">
              <Building2 className="h-8 w-8 text-hr-primary" />
            </div>
          </div>
          <CardTitle className="text-2xl font-bold text-hr-text-primary">
            Create Your Account
          </CardTitle>
          <CardDescription className="text-hr-text-secondary">
            Join our HR management platform
          </CardDescription>
        </CardHeader>

        <CardContent>
          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="admin" className="flex items-center gap-2">
                <Building2 className="h-4 w-4" />
                Admin (Company Owner)
              </TabsTrigger>
              <TabsTrigger value="employee" className="flex items-center gap-2">
                <User className="h-4 w-4" />
                Employee
              </TabsTrigger>
            </TabsList>

            <TabsContent value="admin" className="space-y-4 mt-6">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
                <h4 className="font-medium text-blue-900 mb-2">Admin Registration</h4>
                <p className="text-sm text-blue-700">
                  As an admin, you'll create a new company and become its administrator. 
                  You'll be able to manage employees, departments, and company settings.
                </p>
              </div>

              <form onSubmit={adminForm.handleSubmit(onAdminSubmit)} className="space-y-4">
                <div>
                  <Label htmlFor="admin-name">Full Name</Label>
                  <Input
                    id="admin-name"
                    {...adminForm.register("name")}
                    disabled={isLoading}
                    placeholder="Enter your full name"
                  />
                  {adminForm.formState.errors.name && (
                    <p className="text-sm text-red-600">{adminForm.formState.errors.name.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="admin-email">Email</Label>
                  <Input
                    id="admin-email"
                    type="email"
                    {...adminForm.register("email")}
                    disabled={isLoading}
                    placeholder="Enter your email address"
                  />
                  {adminForm.formState.errors.email && (
                    <p className="text-sm text-red-600">{adminForm.formState.errors.email.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="admin-password">Password</Label>
                  <Input
                    id="admin-password"
                    type="password"
                    {...adminForm.register("password")}
                    disabled={isLoading}
                    placeholder="Create a secure password"
                  />
                  {adminForm.formState.errors.password && (
                    <p className="text-sm text-red-600">{adminForm.formState.errors.password.message}</p>
                  )}
                </div>

                <Button
                  type="submit"
                  className="w-full bg-hr-primary hover:bg-hr-primary/90"
                  disabled={isLoading}
                >
                  {isLoading ? "Creating Account..." : "Create Admin Account"}
                </Button>
              </form>
            </TabsContent>

            <TabsContent value="employee" className="space-y-4 mt-6">
              <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
                <h4 className="font-medium text-green-900 mb-2">Employee Registration</h4>
                <p className="text-sm text-green-700">
                  Create your employee account to browse and apply for jobs across different companies. 
                  You can update your profile later and request permissions from company admins.
                </p>
              </div>

              <form onSubmit={employeeForm.handleSubmit(onEmployeeSubmit)} className="space-y-4">
                <div>
                  <Label htmlFor="emp-name">Full Name</Label>
                  <Input
                    id="emp-name"
                    {...employeeForm.register("name")}
                    disabled={isLoading}
                    placeholder="Enter your full name"
                  />
                  {employeeForm.formState.errors.name && (
                    <p className="text-sm text-red-600">{employeeForm.formState.errors.name.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="emp-email">Email</Label>
                  <Input
                    id="emp-email"
                    type="email"
                    {...employeeForm.register("email")}
                    disabled={isLoading}
                    placeholder="Enter your email address"
                  />
                  {employeeForm.formState.errors.email && (
                    <p className="text-sm text-red-600">{employeeForm.formState.errors.email.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="emp-password">Password</Label>
                  <Input
                    id="emp-password"
                    type="password"
                    {...employeeForm.register("password")}
                    disabled={isLoading}
                    placeholder="Create a secure password"
                  />
                  {employeeForm.formState.errors.password && (
                    <p className="text-sm text-red-600">{employeeForm.formState.errors.password.message}</p>
                  )}
                </div>

                <Button
                  type="submit"
                  className="w-full bg-hr-success hover:bg-hr-success/90"
                  disabled={isLoading}
                >
                  {isLoading ? "Creating Account..." : "Create Employee Account"}
                </Button>
              </form>
            </TabsContent>
          </Tabs>

          <div className="mt-6 text-center">
            <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground">
              <Link href="/login" className="flex items-center gap-1 text-hr-primary hover:underline">
                <ArrowLeft className="h-3 w-3" />
                Back to Login
              </Link>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/user-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { UserPlus, Edit, Trash2, Search } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useAuth } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";

export default function UserManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);

  const { data: users = [], isLoading } = useQuery({
    queryKey: ['/api/users'],
  });

  const { data: companies = [] } = useQuery({
    queryKey: ['/api/admin/companies'],
    enabled: user?.role === 'system_admin',
  });

  const createUserMutation = useMutation({
    mutationFn: (userData: any) => apiRequest('/api/users', { method: 'POST', body: userData }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
      setIsCreateDialogOpen(false);
      toast({ title: "User created successfully" });
    },
    onError: () => {
      toast({ title: "Failed to create user", variant: "destructive" });
    },
  });

  const updateUserMutation = useMutation({
    mutationFn: ({ id, ...data }: any) => apiRequest(`/api/users/${id}`, { method: 'PUT', body: data }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
      toast({ title: "User updated successfully" });
    },
    onError: () => {
      toast({ title: "Failed to update user", variant: "destructive" });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: (id: number) => apiRequest(`/api/users/${id}`, { method: 'DELETE' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
      toast({ title: "User deleted successfully" });
    },
    onError: () => {
      toast({ title: "Failed to delete user", variant: "destructive" });
    },
  });

  const filteredUsers = users.filter((u: any) =>
    u.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
    u.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCreateUser = (formData: FormData) => {
    const userData = {
      username: formData.get('username'),
      email: formData.get('email'),
      password: formData.get('password'),
      role: formData.get('role'),
      companyId: user?.role === 'system_admin' ? 
        parseInt(formData.get('companyId') as string) : 
        user?.companyId,
    };
    createUserMutation.mutate(userData);
  };

  const toggleUserStatus = (userId: number, isActive: boolean) => {
    updateUserMutation.mutate({ id: userId, isActive: !isActive });
  };

  if (isLoading) {
    return <div>Loading users...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">User Management</h1>
          <p className="text-muted-foreground">
            Manage user accounts and permissions
          </p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <UserPlus className="mr-2 h-4 w-4" />
              Add User
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New User</DialogTitle>
            </DialogHeader>
            <form onSubmit={(e) => {
              e.preventDefault();
              handleCreateUser(new FormData(e.currentTarget));
            }} className="space-y-4">
              <div>
                <label className="text-sm font-medium">Username</label>
                <Input name="username" required />
              </div>
              <div>
                <label className="text-sm font-medium">Email</label>
                <Input name="email" type="email" required />
              </div>
              <div>
                <label className="text-sm font-medium">Password</label>
                <Input name="password" type="password" required />
              </div>
              <div>
                <label className="text-sm font-medium">Role</label>
                <Select name="role" required>
                  <SelectTrigger>
                    <SelectValue placeholder="Select role" />
                  </SelectTrigger>
                  <SelectContent>
                    {user?.role === 'system_admin' && (
                      <SelectItem value="system_admin">System Admin</SelectItem>
                    )}
                    <SelectItem value="admin">Admin</SelectItem>
                    <SelectItem value="employee">Employee</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              {user?.role === 'system_admin' && (
                <div>
                  <label className="text-sm font-medium">Company</label>
                  <Select name="companyId" required>
                    <SelectTrigger>
                      <SelectValue placeholder="Select company" />
                    </SelectTrigger>
                    <SelectContent>
                      {companies.map((company: any) => (
                        <SelectItem key={company.id} value={company.id.toString()}>
                          {company.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              )}
              <div className="flex gap-2">
                <Button type="submit" disabled={createUserMutation.isPending}>
                  {createUserMutation.isPending ? 'Creating...' : 'Create User'}
                </Button>
                <Button type="button" variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
                  Cancel
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Users</CardTitle>
          <div className="flex items-center space-x-2">
            <Search className="h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search users..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="max-w-sm"
            />
          </div>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>User</TableHead>
                <TableHead>Role</TableHead>
                <TableHead>Company</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Created</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredUsers.map((user: any) => (
                <TableRow key={user.id}>
                  <TableCell>
                    <div>
                      <div className="font-medium">{user.username}</div>
                      <div className="text-sm text-muted-foreground">{user.email}</div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={
                      user.role === 'system_admin' ? 'destructive' :
                      user.role === 'admin' ? 'default' : 'secondary'
                    }>
                      {user.role.replace('_', ' ')}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    {user.companyId ? `Company ${user.companyId}` : 'System User'}
                  </TableCell>
                  <TableCell>
                    <Badge variant={user.isActive ? 'default' : 'secondary'}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    {new Date(user.createdAt).toLocaleDateString()}
                  </TableCell>
                  <TableCell>
                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => toggleUserStatus(user.id, user.isActive)}
                        disabled={updateUserMutation.isPending}
                      >
                        {user.isActive ? 'Deactivate' : 'Activate'}
                      </Button>
                      {(user?.role === 'system_admin' || user.companyId === user?.companyId) && (
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => deleteUserMutation.mutate(user.id)}
                          disabled={deleteUserMutation.isPending}
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      )}
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

